//задача 14#include<iostream>#include<cmath>using namespace std;int main() {	setlocale(LC_ALL, "rus");	float a, b, h;	const float eps = 0.0001		cout << "ВВЕДИТЕ a\n";	cin >> a;	cout << "ВВЕДИТЕ b\n";	cin >> b;	cout << "ВВЕДИТЕ h\n";	cin >> h;		if (a > b) swap(a, b);	for (float x = a; x < b + h; x += h) {		if (x >= -1 && x <= 1) {			cout << "ЛОГАРИФМ ИЗ НЕПОЛОЖИТЕЛЬНОГО\n";		}		if (x < 1) {			cout << "КОРЕНЬ ИЗ ОТРИЦАТЕЛЬНОГО\n";		}		if ((x - 2)< eps) {			cout << "ДЕЛЕНИЕ НА НОЛЬ\n";		}		else {			float y = log (x*x - 1) / (1-sqrt(x - 1);			cout << "x = ";			cout << x;			cout << " y = ";			cout << y ;		}	}