//Напечатать первые 𝑁 простых чисел.#include<iostream>using namespace std;int main(){    int n;    cin >> n;    int k = 2;    while (n)    {        n--;        cout << k << ' ';        bool fl = true;        while (fl)        {            fl = false;            k++;            int s = k / 2;            for (int j = 2; j <= s; j++)            {                if (k % j == 0)                {                    fl = true;                    break;                }            }        }    }    return 0;}